const initialState = {
  fecha: '1969-01-12',
  bid: 700.00,
  ask: 700.30,
  ops: [
    {numop: 1, cliente: 'Santander', fxrate: 'USDCLP', cv: 'Compra', monto: 1000000, precio: 700},
    {numop: 2, cliente: 'Estado', fxrate: 'USDCLP', cv: 'Venta', monto: 3000000, precio: 701},
  ],
  nextNumop: 3,
  posicion: -1000000,
  ppcompra: 700,
  ppventa: 701,
  resTrading: 1000000,
  resPos: -1000000
}

const sumaCompras = (acum, op) => {
  if (op.cv === 'Compra') {
    return acum + op.monto
  } else {
    return acum
  }
}

const sumaVentas = (acum, op) => {
  if (op.cv === 'Compra') {
    return acum
  } else {
    return acum - op.monto
  }
}


const update = (state = initialState, action) => {
  if (action.type === 'PUNTA') {
    const compras = state.ops.reduce(sumaCompras, 0)
    const ventas = state.ops.reduce(sumaVentas, 0)
    const posicion = compras + ventas
    return {...state, bid: action.bid, ask: action.ask, posicion: posicion}
  }
  return state
}

export default update
