        newOpsArray.push(state.ops[key])
      }
    }
    const compras = newOpsArray.reduce(sumaCompras, 0)
    const ventas = newOpsArray.reduce(sumaVentas, 0)
    const posicion = compras - ventas
    const compPond = newOpsArray.reduce(comprasPonderadas, 0)
    const ventPond = newOpsArray.reduce(ventasPonderadas, 0)
    const ppcompra = compras > 0 ? compPond / compras : 0
    const ppventa = ventas > 0 ? ventPond / ventas : 0
    const resTrading = Math.round(Math.min(compras, ventas) * (ppventa - ppcompra))
    const resPos = Math.round(posicion > 0 ? posicion * (state.bid - ppcompra) :
      - posicion * (ppventa - state.ask))
    return {...state, ops: newOps, posicion: posicion,
      compras: compras, ppcompra: ppcompra,
      ventas: ventas, ppventa: ppventa,
      resTrading: resTrading, resPos: resPos}
  }

  if (action.type === 'EDIT_OP') {
    const whichOp = parseInt(action.whichOp)
    const modOp = state.ops.filter(() => state.ops.id === whichOp)
    return {...state, ops: [...state.ops, ]}
  }

  return state
}

