const initialState = {
  fecha: '1969-01-12',
  bid: 700.00,
  ask: 700.30,
  ops: [
    {numop: 1, cliente: 'Santander', fxrate: 'USDCLP', cv: 'Compra', monto: 1000000, precio: 700},
    {numop: 2, cliente: 'Estado', fxrate: 'USDCLP', cv: 'Venta', monto: 3000000, precio: 701},
    {numop: 3, cliente: 'Estado', fxrate: 'USDCLP', cv: 'Venta', monto: 3000000, precio: 702},
    {numop: 4, cliente: 'Estado', fxrate: 'USDCLP', cv: 'Compra', monto: 1000000, precio: 701},

  ],
  nextNumop: 5,
  posicion: -1000000,
  compras: 2000000,
  ppcompra: 700,
  ventas: 6000000,
  ppventa: 701,
  resTrading: 1000000,
  resPos: -1000000
}

const sumaCompras = (acum, op) => {
  if (op.cv === 'Compra') {
    return acum + op.monto
  } else {
    return acum
  }
}

const comprasPonderadas = (acum, op) => {
  if (op.cv === 'Compra') {
    return acum + op.monto * op.precio
  } else {
    return acum
  }
}

const ventasPonderadas = (acum, op) => {
  if (op.cv === 'Venta') {
    return acum + op.monto * op.precio
  } else {
    return acum
  }
}

const sumaVentas = (acum, op) => {
  if (op.cv === 'Compra') {
    return acum
  } else {
    return acum + op.monto
  }
}


const update = (state = initialState, action) => {
  if (action.type === 'PUNTA') {
    const compras = state.ops.reduce(sumaCompras, 0)
    const ventas = state.ops.reduce(sumaVentas, 0)
    const posicion = compras - ventas
    const compPond = state.ops.reduce(comprasPonderadas, 0)
    const ventPond = state.ops.reduce(ventasPonderadas, 0)
    const ppcompra = compras > 0 ? compPond / compras : 0
    const ppventa = ventas > 0 ? ventPond / ventas : 0
    const resTrading = Math.round(Math.min(compras, ventas) * (ppventa - ppcompra))
    const resPos = Math.round(posicion > 0 ? posicion * (action.bid - ppcompra) : - posicion * (ppventa - action.ask))
    return {...state, bid: action.bid, ask: action.ask, posicion: posicion,
      ppcompra: ppcompra, ppventa: ppventa, resTrading: resTrading, resPos: resPos}
  }
  if (action.type === 'ELIM_OP') {
    // eslint-disable-next-line no-restricted-globals
    const sure = confirm('¿Está seguro de eliminar la operación ' + action.whichOp + '?')
    if (!sure) {
      return state
    }
    const whichOp = parseInt(action.whichOp)
    const newOps = state.ops.filter((op) => op.numop !== whichOp)
    const compras = newOps.reduce(sumaCompras, 0)
    const ventas = newOps.reduce(sumaVentas, 0)
    const posicion = compras - ventas
    const compPond = newOps.reduce(comprasPonderadas, 0)
    const ventPond = newOps.reduce(ventasPonderadas, 0)
    const ppcompra = compras > 0 ? compPond / compras : 0
    const ppventa = ventas > 0 ? ventPond / ventas : 0
    const resTrading = Math.round(Math.min(compras, ventas) * (ppventa - ppcompra))
    const resPos = Math.round(posicion > 0 ? posicion * (state.bid - ppcompra) :
      - posicion * (ppventa - state.ask))
    return {...state, ops: newOps, posicion: posicion,
      ppcompra: ppcompra, ppventa: ppventa, resTrading: resTrading, resPos: resPos}
  }
  return state
}

export default update
